                           header=TRUE, 
                           fill=TRUE, 
                           stringsAsFactor=FALSE)


#cov_1_table <- subset(cov_1_table, c((cov_1_table$end - cov_1_table$start) > minRange ))
#cov_2_table <- subset(cov_2_table, c((cov_2_table$end - cov_2_table$start) > minRange ))
