  cov.select.1 <- cov_1$df
  max.cov.1 <- cov_1$max
  min.cov.1 <- cov_1$min
  median.cov.1 <- cov_1$median
  
  cov_2 <- gen_data_4plotting( cov_2_table, c("chr", "range", "diff"))
  cov.select.2 <- cov_2$df
  max.cov.2 <- cov_2$max
  min.cov.2 <- cov_2$min
